import React, { useState, useRef, useEffect } from 'react';
import { Send } from 'lucide-react';
import './ChatInterface.css';

const starterPrompts = [
  "Summarize the latest paper on quantum gravity",
  "Compare two papers on string theory",
  "Generate BibTeX citations",
  "What‚Äôs a good intro to General Relativity?"
];

const ChatInterface = ({ messages, onUpdateMessages, onSendMessage }) => {
  const [input, setInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const bottomRef = useRef(null);

  // Auto-scroll on message update
  useEffect(() => {
    if (bottomRef.current) {
      bottomRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  const handleSend = async (e) => {
    e.preventDefault();
    const trimmed = input.trim();
    if (!trimmed) return;

    const userMsg = { role: 'user', content: trimmed };
    const updatedMessages = [...messages, userMsg];
    onUpdateMessages(updatedMessages);
    setInput('');
    setIsTyping(true);

    try {
      const aiResponse = await onSendMessage(trimmed);
      const botMsg = { role: 'assistant', content: aiResponse };
      onUpdateMessages([...updatedMessages, botMsg]);
    } catch (err) {
      onUpdateMessages([
        ...updatedMessages,
        { role: 'assistant', content: '‚ö†Ô∏è Failed to get response from Cosmico.' },
      ]);
    } finally {
      setIsTyping(false);
    }
  };

  return (
    <div className="chatbox">
      <div className="chat-content">
        {/* ‚ú® Suggestions (only if no messages yet) */}
        {messages.length === 0 && (
          <div className="chat-suggestions">
            {starterPrompts.map((prompt, i) => (
              <button key={i} onClick={() => setInput(prompt)}>
                {prompt}
              </button>
            ))}
          </div>
        )}

        {/* üó®Ô∏è Messages */}
        {messages.map((msg, i) => (
          <div key={i} className={`chat-msg ${msg.role === 'user' ? 'user' : 'bot'}`}>
            <div className="msg-bubble">{msg.content}</div>
          </div>
        ))}

        {/* ‚åõ Typing animation */}
        {isTyping && (
          <div className="chat-msg bot">
            <div className="msg-bubble typing">
              <span className="dot" />
              <span className="dot" />
              <span className="dot" />
            </div>
          </div>
        )}

        <div ref={bottomRef} />
      </div>

      {/* üìù Input */}
      <form className="chat-input-area" onSubmit={handleSend}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask Cosmico anything..."
          className="chat-input"
        />
        <button type="submit" disabled={!input.trim()} className="chat-send">
          <Send size={18} />
        </button>
      </form>
    </div>
  );
};

export default ChatInterface;
